<?php defined ('IN_KEKE' ) or die ( 'Access Denied' );
	class Keke_witkey_nav  extends Model {
	    protected static $_data = array ();
	     function  __construct(){ 			 parent::__construct ( 'witkey_nav' );		 }	    
